// SignUp.js
import React, { useState } from 'react';
import axios from 'axios';
import './SignUp.css';

const SignUp = () => {
  const [email, setEmail] = useState('');
  const [otp, setOtp] = useState('');
  const [verificationStatus, setVerificationStatus] = useState('');

  const handleSendOTP = async () => {
    try {
      await axios.post('http://localhost:5000/sendotp', { email });
      setVerificationStatus('OTP sent successfully');
    } catch (error) {
      console.error('Failed to send OTP:', error);
      setVerificationStatus('Failed to send OTP. Please try again.');
    }
  };

  const handleVerifyOTP = async () => {
    try {
      const response = await axios.post('http://localhost:5000/verifyotp', { otp });
      setVerificationStatus(response.data.message);
    } catch (error) {
      console.error('Failed to verify OTP:', error);
      setVerificationStatus('Invalid OTP. Please try again.');
    }
  };

  return (
    <div className="form-container">
      <h2>Sign Up</h2>
      <label htmlFor="email">Email:</label>
      <input type="email" id="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
      <button onClick={handleSendOTP}>Send OTP</button>
      {verificationStatus && <p>{verificationStatus}</p>}
      <label htmlFor="otp">OTP:</label>
      <input type="text" id="otp" value={otp} onChange={(e) => setOtp(e.target.value)} required />
      <button onClick={handleVerifyOTP}>Verify OTP</button>
    </div>
  );
};

export default SignUp;
