// SignUp.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './SignUp.css'; // Import the CSS file for SignUp styling

const SignUp = () => {
  const [verificationSent, setVerificationSent] = useState(false);
  const [otp, setOtp] = useState('');
  const [isMobileVerified, setIsMobileVerified] = useState(false);
  const [isEmailVerified, setIsEmailVerified] = useState(false);
  const [isSigningUp, setIsSigningUp] = useState(false);

  const handleVerify = () => {
    // Simulate sending OTP
    // For demonstration purposes, let's set verificationSent to true immediately
    setVerificationSent(true);
  };

  const handleOtpSubmit = () => {
    // Here you would typically verify the OTP with a backend service
    // For simplicity, let's assume the OTP entered is correct
    setIsMobileVerified(true);
    setIsEmailVerified(true);
  };

  const handleSignUp = () => {
    // Simulate signing up
    setIsSigningUp(true);
    setTimeout(() => {
      alert('User signed up successfully!');
    }, 1000);
  };

  return (
    <div className="form-container">
      <h2>Sign Up</h2>
      <form onSubmit={handleSignUp}>
        <label htmlFor="username">Username:</label>
        <input type="text" id="username" name="username" required />
        <label htmlFor="email">Email:</label>
        <div className="input-with-button">
          <input type="email" id="email" name="email" required />
          <button type="button" className="verify-button" onClick={handleVerify} disabled={verificationSent}>
            {verificationSent ? 'Verified' : 'Verify'}
          </button>
        </div>
        <label htmlFor="mobile">Mobile Number:</label>
        <div className="input-with-button">
          <input type="text" id="mobile" name="mobile" required />
          <button type="button" className="verify-button" onClick={handleVerify} disabled={verificationSent}>
            {verificationSent ? 'Verified' : 'Verify'}
          </button>
        </div>
        <label htmlFor="password">Password:</label>
        <input type="password" id="password" name="password" required />
        <label htmlFor="confirmPassword">Confirm Password:</label>
        <input type="password" id="confirmPassword" name="confirmPassword" required />
        <button type="submit" disabled={!isMobileVerified || !isEmailVerified || isSigningUp}>
          {isSigningUp ? 'Signing Up...' : 'Sign Up'}
        </button>
      </form>
      <p className="link-text">Already a user? <Link to="/login">Login</Link></p>
      {isMobileVerified && isEmailVerified && <p className="verification-message">Mobile number and email verified!</p>}
      {!isMobileVerified && !isEmailVerified && verificationSent && (
        <div className="otp-container">
          <input type="text" placeholder="Enter OTP" value={otp} onChange={(e) => setOtp(e.target.value)} />
          <button type="button" onClick={handleOtpSubmit}>Submit</button>
        </div>
      )}
    </div>
  );
}

export default SignUp;
