// SignUp.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const SignUp = () => {
  const [verificationSent, setVerificationSent] = useState(false);
  const [otp, setOtp] = useState('');
  const [isMobileVerified, setIsMobileVerified] = useState(false);
  const [isEmailVerified, setIsEmailVerified] = useState(false);

  const handleVerify = () => {
    // Simulate sending OTP
    // For demonstration purposes, let's set verificationSent to true immediately
    setVerificationSent(true);
  };

  const handleOtpSubmit = () => {
    // Here you would typically verify the OTP with a backend service
    // For simplicity, let's assume the OTP entered is correct
    setIsMobileVerified(true);
    setIsEmailVerified(true);
  };

  return (
    <div className="form-container">
      <h2>Sign Up</h2>
      <form>
        <label htmlFor="username">Username:</label>
        <input type="text" id="username" name="username" />
        <label htmlFor="email">Email:</label>
        <input type="email" id="email" name="email" />
        <button type="button" onClick={handleVerify} disabled={verificationSent}>
          {verificationSent ? 'Verified' : 'Verify'}
        </button>
        <label htmlFor="mobile">Mobile Number:</label>
        <input type="text" id="mobile" name="mobile" />
        <button type="button" onClick={handleVerify} disabled={verificationSent}>
          {verificationSent ? 'Verified' : 'Verify'}
        </button>
        <label htmlFor="password">Password:</label>
        <input type="password" id="password" name="password" />
        <label htmlFor="confirmPassword">Confirm Password:</label>
        <input type="password" id="confirmPassword" name="confirmPassword" />
        <button type="submit">Sign Up</button>
      </form>
      <p className="link-text">Already a user? <Link to="/login">Login</Link></p>
      {isMobileVerified && isEmailVerified && <p className="verification-message">Mobile number and email verified!</p>}
      {!isMobileVerified && !isEmailVerified && verificationSent && (
        <div className="otp-container">
          <input type="text" placeholder="Enter OTP" value={otp} onChange={(e) => setOtp(e.target.value)} />
          <button type="button" onClick={handleOtpSubmit}>Submit</button>
        </div>
      )}
    </div>
  );
}

export default SignUp;
